简单版

function Pub(){
  this.handlers = {}
}

Pub.prototype = {
  //注册  绑定名称 和 回调
  on(name, cb){
    console.log('name: ', name);
    var self  = this;
    if( !(name in self.handlers)){
      self.handlers[name] = [];
    }
    self.handlers[name].push(cb)
    return this
  },
  //激活
  emit(name){
    var self = this;
    var handlerArg = Array.prototype.slice.call(arguments, 1);

    for(let i in self.handlers[name]){
      self.handlers[name][i].apply(self, handlerArg);
    }
    return self;
  },
    remove(name, cb){
     //移除事件
     console.log('name, cb: ', name, cb);

     let fns = this.handlers[name]
     if(!fns || !cb){
          return false
     }
     for(let l = fns.length -1; l >=0; l--){
          let _fn = fns[l];
          console.log('_fn: ', _fn);
          console.log('name, cb: ',  cb == _fn );

          if(_fn()  == cb()){
               console.log('1')
               fns.splice(l,1)
          };
     }
  }
}

