loadings = {},
queue = [],
scripts = doc.getElementsTagName('script'),
root = scripts[scripts.length-1].src,
baseUrl = root.slice(0, root.lastIndexOf('/') + 1);
function Module(path, deps, factory){
  this.id = path;
  uitls.addLoading(this.deps);
  cache[path] = this;
}



var utils = {
  addLoading(){
