实现proxy劫持
class C {
    constructor() {
        this.children = '1'
    }
    @enumerable(false)
    get method() {
        return this.children.length;
    }
}

function enumerable(value) {
    return function(target, key, descriptor) {
        descriptor.enumerable = value;
        return descriptor;
    }
}

var c = new C();
