const buf = new Buffer([1,2,3])
console.log('buf', buf)
//下面这些是在Buffer构造函数中使用到的一些基本的方法：
//smalloc为内存操作的对象
var smalloc = process.binding('smalloc');
//util为工具函数，包含一些基本的操作方法
var util = require('util');
//alloc对象用于生成一段内存的方法
var alloc = smalloc.alloc;
